mapper.select:
select * 
from (select rownum rnum, sub.*
		from (select * from board) sub 
	)

where rnum >= #{from} and rnum <= #{to}

select * 
from (select rownum rnum, sub.*
		from (select * from board where user_no=#{userNo}) sub 
	)

where rnum >= #{from} and rnum <= #{to}

select * 
from (select rownum rnum, sub.*
		from (select * from board where user_no=#{userNo} and category_no = #{categoryNo}) sub 
	)

where rnum >= #{from} and rnum <= #{to}

select * 
from (select rownum rnum, sub.*
		from (select * from board where title like '%'||#{title}||'%' or content like '%'||#{content}||'%') sub 
	)		
where rnum >= #{from} and rnum <= #{to}

select * 
from (select rownum rnum, sub.*
		from (select * from board where title like '%'||#{title}||'%' or content like '%'||#{content}||'%' and category_no = #{categoryNo}) sub 
	)		
where rnum >= #{from} and rnum <= #{to}
